.j_batch_delete {
	position: static;
	float: none;
	align-self: center;
	height: 20px;
	width: 20px;
	top: auto;
	right: auto;
	order: 1;
	padding: 10px;
}

.j_batch_delete:before {
	content: '';
	position: absolute;
	height: 100%;
	right: 40px;
	top: 0;
	border-left: 1px solid rgba(0, 0, 0, .05);
}

.j_batch_delete_select {
	position: absolute;
	height: 20px;
	width: 20px;
	opacity: 0;
	cursor: pointer;
	z-index: 1;
}

.batch_delete_text {
	box-sizing: border-box;
	height: 20px;
	width: 20px;
	margin: 0 !important;
	font-size: 0 !important;
	border-radius: 4px;
	background: rgba(0, 0, 0, .1);
	border: 1px solid rgba(0, 0, 0, .1);
	transition: background .4s ease;
	line-height: 18px;
	text-align: center;
}

.batch_delete_text:before {
	content: "\e5ca";
	font-family: 'Material Icons';
	font-size: 16px;
	font-weight: bold;
	display: inline-block !important;
	vertical-align: middle;
	color: #fff;
	opacity: 0;
	transition: opacity .4s ease;
}

.batch_delete_text:after {
	content: "";
	position: absolute;
	width: 100%;
	height: 100%;
	left: 0;
	top: 0;
	border-left: 4px solid var(--m-ecolor);
	pointer-events: none;
	opacity: 0;
	transition: opacity .4s ease;
}

.j_batch_delete_select:checked {
	counter-increment: selected-items;
}

.j_batch_delete_select:checked+.batch_delete_text {
	background: var(--m-ecolor) !important;
}

.j_batch_delete_select:checked+.batch_delete_text:before,
.j_batch_delete_select:checked+.batch_delete_text:after {
	opacity: 1;
}

.j_batch_delete_btn_area {
	float: none;
	position: fixed;
	z-index: 10;
	display: flex;
	align-items: center;
	bottom: 0;
	width: var(--m-mainwidth);
	margin: 0 !important;
	padding: 10px 0 !important;
	background: rgb(240, 240, 240) !important;
	border-top: 4px solid rgba(0, 0, 0, .1) !important;
	color: #666;
}

.j_batch_delete_btn_area:before {
	content: '贴子管理模式：已选择 'counter(selected-items)' 个贴子';
	flex: 1;
	padding: 0 20px;
	font-size: 14px;
}

.j_batch_delete_btn_area:after {
	content: '';
